class Solution {
    public static Node moveToFront(Node head) {
        // Case 1: Empty list or single element ‚Äî nothing to move
        if (head == null || head.next == null) {
            return head;
        }

        // Initialize pointers
        Node prev = null;
        Node curr = head;

        // Traverse to the last node
        while (curr.next != null) {
            prev = curr;
            curr = curr.next;
        }

        // At this point, curr is the last node, prev is second-last
        prev.next = null;   // Remove last node from its current position
        curr.next = head;   // Point last node to old head
        return curr;        // Last node becomes new head
    }
}
