        if (current != null) {
            Node prevNode = current.prev;
            Node nextNode = current.next;

            if (prevNode != null) {
                prevNode.next = nextNode;
            }
            if (nextNode != null) {
                nextNode.prev = prevNode;
            }
        }

        return head;
    }
}
