```java
import java.util.*;

class Solution {
    public String clearStars(String s) {
        Deque<Character> stack = new ArrayDeque<>();
        TreeMap<Character, Integer> counts = new TreeMap<>();

        for (char ch : s.toCharArray()) {
            if (ch != '*') {
                stack.push(ch);
                counts.put(ch, counts.getOrDefault(ch, 0) + 1);
            } else {
                // Find smallest character in stack
                char smallest = counts.firstKey();

                // Remove smallest from stack from top to bottom
                Deque<Character> tempStack = new ArrayDeque<>();
                boolean removed = false;

                while (!stack.isEmpty()) {
                    char top = stack.pop();
                    if (!removed && top == smallest) {
                        // Remove this character
                        int c = counts.get(top);
                        if (c == 1) counts.remove(top);
                        else counts.put(top, c - 1);
                        removed = true;
                        break;
                    } else {
                        tempStack.push(top);
                    }
                }

                // Push back the other characters
                while (!tempStack.isEmpty()) {
                    char c = tempStack.pop();
                    stack.push(c);
                }
            }
        }

        // Stack currently has the characters in reverse order
        StringBuilder sb = new StringBuilder();
        while (!stack.isEmpty()) {
            sb.append(stack.pop());
        }
        return sb.reverse().toString();
    }
}
```

