```javascript
const menuItems = [
  { id: 1, name: "Veg Sandwich", price: 40 },
  { id: 2, name: "Paneer Roll", price: 60 },
  { id: 3, name: "Burger", price: 50 },
  { id: 4, name: "Cold Coffee", price: 30 },
  { id: 5, name: "Samosa", price: 15 },
  { id: 6, name: "Maggie", price: 35 }
];

const cart = {};

function renderMenu() {
  const menu = document.getElementById('menu');
  menuItems.forEach(item => {
    const div = document.createElement('div');
    div.className = 'item';
    div.innerHTML = `
      <h3>${item.name}</h3>
      <p>‚Çπ${item.price}</p>
      <button onclick="addToCart(${item.id})">Add to Cart</button>
    `;
    menu.appendChild(div);
  });
}

function addToCart(itemId) {
  const item = menuItems.find(i => i.id === itemId);
  if (!cart[itemId]) {
    cart[itemId] = { ...item, quantity: 1 };
  } else {
    cart[itemId].quantity += 1;
  }
  renderCart();
}

function updateQuantity(itemId, change) {
  if (cart[itemId]) {
    cart[itemId].quantity += change;
    if (cart[itemId].quantity <= 0) {
      delete cart[itemId];
    }
  }
  renderCart();
}

function removeFromCart(itemId) {
  delete cart[itemId];
  renderCart();
}

function renderCart() {
  const cartDiv = document.getElementById('cart');
  cartDiv.innerHTML = '';
  let total = 0;
  let count = 0;

  Object.values(cart).forEach(item => {
    total += item.price * item.quantity;
    count += item.quantity;

    const div = document.createElement('div');
    div.className = 'cart-item';
    div.innerHTML = `
      <h4>${item.name}</h4>
      <p>Price: ‚Çπ${item.price}</p>
      <p>Quantity: ${item.quantity}</p>
      <div class="cart-controls">
        <div>
          <button onclick="updateQuantity(${item.id}, 1)">+</button>
          <button onclick="updateQuantity(${item.id}, -1)">‚àí</button>
        </div>
        <button class="remove-btn" onclick="removeFromCart(${item.id})">Remove</button>
      </div>
    `;
    cartDiv.appendChild(div);
  });

  document.getElementById('totalPrice').innerText = total;
  document.getElementById('cart-count').innerText = count;
}

renderMenu();
