  </nav>

  <div class="container">
    <div class="search-bar">
      <input type="text" id="search" placeholder="Search for food...">
    </div>

    <div class="menu" id="menu"></div>

    <div class="cart-section">
      <h2>Your Cart</h2>
      <div id="cart"></div>
      <div class="total">Total: ‚Çπ<span id="totalPrice">0</span></div>
      <button class="clear-cart" onclick="clearCart()">Clear Cart</button>
      <button class="order-btn" onclick="placeOrder()">Order Now</button>
    </div>
  </div>

  <script>
    const menuItems = [
      { id: 1, name: "Veg Sandwich", price: 40, image: "https://source.unsplash.com/400x300/?sandwich" },
      { id: 2, name: "Paneer Roll", price: 60, image: "https://source.unsplash.com/400x300/?roll" },
      { id: 3, name: "Burger", price: 50, image: "https://source.unsplash.com/400x300/?burger" },
      { id: 4, name: "Cold Coffee", price: 30, image: "https://source.unsplash.com/400x300/?coffee" },
      { id: 5, name: "Samosa", price: 15, image: "https://source.unsplash.com/400x300/?samosa" },
      { id: 6, name: "Maggie", price: 35, image: "https://source.unsplash.com/400x300/?maggie" }
    ];

    let cart = JSON.parse(localStorage.getItem('canteenCart')) || {};

    function saveCart() {
      localStorage.setItem('canteenCart', JSON.stringify(cart));
    }

    function renderMenu() {
      const menu = document.getElementById('menu');
      menu.innerHTML = '';
      const search = document.getElementById('search').value.toLowerCase();
      menuItems.filter(item => item.name.toLowerCase().includes(search)).forEach(item => {
        const div = document.createElement('div');
        div.className = 'item';
        div.innerHTML = `
          <img src="${item.image}" alt="${item.name}" />
          <h3>${item.name}</h3>
          <p>‚Çπ${item.price}</p>
          <button onclick="addToCart(${item.id})">Add to Cart</button>
        `;
        menu.appendChild(div);
      });
    }

    function addToCart(id) {
      if (!cart[id]) {
        const item = menuItems.find(i => i.id === id);
        cart[id] = { ...item, quantity: 1 };
      } else {
        cart[id].quantity++;
      }
      saveCart();
      renderCart();
    }

    function updateQuantity(id, change) {
      if (cart[id]) {
        cart[id].quantity += change;
        if (cart[id].quantity <= 0) delete cart[id];
        saveCart();
        renderCart();
      }
    }

    function removeItem(id) {
      delete cart[id];
      saveCart();
      renderCart();
    }

    function clearCart() {
      if (confirm("Are you sure you want to clear the cart?")) {
        cart = {};
        saveCart();
        renderCart();
      }
    }

    function placeOrder() {
      if (Object.keys(cart).length === 0) {
        alert("Your cart is empty. Please add items before placing an order.");
        return;
      }

      const confirmation = confirm("Do you want to place the order?");
      if (confirmation) {
        alert("Order placed successfully!");
        cart = {};
        saveCart();
        renderCart();
      }
    }

    function renderCart() {
      const cartDiv = document.getElementById('cart');
      cartDiv.innerHTML = '';
      let total = 0;
      let count = 0;

      Object.values(cart).forEach(item => {
        total += item.price * item.quantity;
        count += item.quantity;

        const div = document.createElement('div');
        div.className = 'cart-item';
        div.innerHTML = `
          <strong>${item.name}</strong>
          <div class="cart-controls">
            <button onclick="updateQuantity(${item.id}, 1)">+</button>
            <span>${item.quantity}</span>
            <button onclick="updateQuantity(${item.id}, -1)">-</button>
            <button class="remove-btn" onclick="removeItem(${item.id})">Remove</button>
          </div>
        `;
        cartDiv.appendChild(div);
      });

      document.getElementById('cart-count').textContent = count;
      document.getElementById('totalPrice').textContent = total;
    }

    document.getElementById('search').addEventListener('input', renderMenu);
    renderMenu();
    renderCart();
  </script>
</body>
</html>"
    }
  ]
}

